import React from 'react';
import { ActivityIndicator, View } from 'react-native';

import Styles from './styles';
import { LoaderProps } from './types';

/**
 * Loader component.
 * @param customLoader - Use custom loader instead of the default.
 * @param loaderColor - Use custom loader color instead of the default.
 * @param style - The loader's custom style.
 */
const Loader: React.FC<LoaderProps> = ({ loaderColor = 'grey', customLoader, style }) => {
  return (
    <View style={[Styles.container, style]}>
      {customLoader ? customLoader : <ActivityIndicator color={loaderColor} size="large" />}
    </View>
  );
};

export default Loader;
